import styled from "styled-components";



export const Section = styled.section `
    width: 100%;
    max-width: 400px;
    height: 400px;
    background: rgba(255,255,255, .95);
    background-color: #151320;
    color: whitesmoke;
    border-radius: 10px;
    border-bottom: 10px solid #633BBC;
    margin-top: 15px;
    position: relative;
    box-shadow: 16px 14px 20px #0000008c;
    @media (max-width: 430px) {
        width: 300px;
    }
`

export const UserLogin = styled.h1 `
  font-size: 25px;
    font-weight: 700;
    margin-top: 10px;
    color: whitesmoke;
    width: 100%;
    text-align: center;
    height: 30px;
`


export const UserAvatar = styled.div `
     width: 20px;
        height: 20px;
        border-radius: 50%;
        
        box-shadow: 4px 5px 4px 0px #151320;


`
